import UIKit

class BitkilerViewController: UIViewController,UITableViewDelegate,UITableViewDataSource {
    @IBOutlet weak var tableView: UITableView!
    
    var bitkiler = [Bitkiler]()
    var bitkiSec : Bitkiler?

    override func viewDidLoad() {
        super.viewDidLoad()
        bitkiEkle()
    }
    func tableView(_ tableView: UITableView, numberOfRowsInSection section: Int) -> Int {
        return bitkiler.count
    }
    func tableView(_ tableView: UITableView, cellForRowAt indexPath: IndexPath) -> UITableViewCell {
        let cell = UITableViewCell()
        cell.textLabel?.text = bitkiler[indexPath.row].adi
        return cell
    }
    func tableView(_ tableView: UITableView, didSelectRowAt indexPath: IndexPath) {
        bitkiSec = bitkiler[indexPath.row]
        self.performSegue(withIdentifier: "toBitkiDetayViewController", sender: nil)
    }
    override func prepare(for segue: UIStoryboardSegue, sender: Any?) {
        if segue.identifier == "toBitkiDetayViewController"{
            let destinationVC = segue.destination as! BitkiDetayViewController
            destinationVC.selectedBitkiler = bitkiSec
        }
    }
    func bitkiEkle()
    {
        let aciyonga = Bitkiler(Adi: "Acı Yonga(Acı Ağaç)", Tanim: "Acı yonga bitkisi, ateş ve kurt düşürmede etkili olan kırmızı çiçekli bir ağaçtır. Orta ve Güney Amerika’nın Antil adalarında yetişen ve gelişen bu ağaç türü acı ağaç veya acı tahta olarak da bilinmektedir. Antil adalarında yetişen 15 m yükseklikteki acı yongalar çıkardıkları kırmızı çiçekleri ile hoş bir görüntü sergilemektedir. Ağacın en büyük özelliği ise acı ağaç özleri sayesinde hiçbir sinek ve haşere’tin yaklaşamamasıdır. Bitkisel bir böcek, sinek ve sivrisinek ilacıdır.", Image: UIImage(named:"aciyonga")!, Faydalari: "İştah açar, hazmı kolaylaştırır. Ateşi düşürür. Tükürük ifrazatını arttırır. Mide, bağırsak, karaciğer ve böbreklerin çalışmasını düzenler. Böbrek sancılarını keser, taşların düşürülmesine yardımcı olur. Bağırsak kurtlarını döker. Kanamaları durdurur. Haşarat kaçırıcı olarak da kullanılır. Fazla kullanılacak olursa; baş dönmesi, mide bulantısı ve kusma yapar.Çok acı olduğu için haşereleri öldürmede ve kaçırmada etkilidir.", BilimselAdi: "Bilimsel Adı : Lignum Quassiae")
        bitkiler.append(aciyonga)
        
        let adacayi = Bitkiler(Adi: "Adaçayı", Tanim: "Adaçayı önemli farmakolojik aktivitelere sahip bileşenleri içeren bir bitkidir. Eski çağlardan beri parfümlerde kullanılmış, lezzet vermek amacıyla yiyeceklere eklenmiş ve günlük beslenmede tedavi amaçlı yer almıştır. “Salvia” kelimesi “iyileşmek için” anlamına gelir. Orta Doğu ve Akdeniz’e özgü aromatik bir bitki olan adaçayı; Avrupa’da geleneksel tıpta sindirim sistemi rahatsızlıkları, öksürük ve bronşit, fazla terleme, yaşa bağlı kognitif hastalıklar, boğaz ağrısı ve deri iltihaplanmasında kullanılmaktadır.", Image: UIImage(named: "adacayi")!, Faydalari: "Adaçayının yaprakları, yağı ve özütü uzun yıllardır geleneksel tıpta pek çok hastalığın tedavisi için kullanılmaktadır. Özellikle sindirim sistemi problemleri, kadınlarda menopoz sonrası ortaya çıkan sıcak basması ve uykusuzluk, ağız yaraları, yara iyileşmesi, adet döngüsünün düzenlenmesi, üst solunum yolu enfeksiyonu ve fazla terlemenin engellenmesinde doğal bir tedavi yöntemi olarak kullanılmaktadır.", BilimselAdi: "Bilimsel Adı : Salvia Officinalis")
       bitkiler.append(adacayi)
        
       let ahlat = Bitkiler(Adi: "Ahlat", Tanim: "Ahlat bitkisi doğa ortamında kendiliğinden yetişen yabani bir armut türüdür.Halk arasında yaban armudu olarak da bilinen Ahlat bitkisi birçok özelliği sayesinde diğer bitki türlerinden ayrılmaktadır.Bu bitki türünü diğer bitki türlerinden ayıran en belirgin nitelik ise hemen her iklimde yetişebilmesidir. Ahlat bitkisinin yetişmesi için iklim şartlarının fazla bir önemi bulunmadığından her iklimde kolaylıkla yetişebilmektedir.Ahlat bitkisine daha çok ormanlık alanlarda ve bozkır alanlarında rastlanmaktadır.", Image: UIImage(named: "ahlat")!, Faydalari: "Vücudumuzda yer alan alkalitleri düzenlemektedir.Sakinleştiricidir.İdrar yollarında meydana gelen sorunları kısa sürede ortadan kaldırmaktadır.İdrar sökücüdür.İshal kesicidir.Zehirli haşere sokmalarında meydana gelen zehirin etkisini yok etmektedir.Kalbin çalışma işlevinin kuvvetlenmesini sağlamaktadır.", BilimselAdi: "Bilimsel Adı : Pyrus Elaeagnifolia")
        bitkiler.append(ahlat)
        

        let avsarotu = Bitkiler(Adi: "Avşar Otu", Tanim: "Avşar otunun halk arasındaki diğer isimleri azak eğeri, eğir otu ve meyhaneci otudur. Fakat Avşar otunun Latince ismi asarum europaeumdur. Avşar otu nisan ile mayıs aylarında siyahı andıran yani erguvani renkte çiçekler açar. Avşar otunun yapraklarının şekli böbrek şeklindedir ve sürünücü özelliktedir. Bu yaprakları ezdiğiniz zaman baharlı bir koku ortaya çıkar. Avşar otunun en çok sevdiği yer ise orman kenarlarındaki gölgelik yerlerdir.", Image: UIImage(named:"avsarotu")!, Faydalari: "Avşar otu sindirim sistemini tahriş edip kusturma özelliğine sahip bir bitkidir.Kişide kusturma etkisini ipeka denen bitki keşfedilmeden önce hep Avşar otu ile telafi ederlerdi.Avşar otu eski zamanlarda çok fazla alkol alan kişileri kusturmak amacı ile kullanıldığı için halk arasında meyhaneci otu diye anılmaktadır.Avşar otunun kusturucu etkisinden yararlanmak istiyorsanız 7 adet Avşar otu taze yaprağının suyunu sıkıp su yada süt ile için.Eğer suyunu içmek istemiyorsanız Avşar otunun kurutulmuş köklerini toz haline getirip 1 yada 2 gram kadar almanız yeterli olacaktır.Tabiki Avşar otu sadece kusturucu etkiye sahip bir bitki değildir. Doğada birçok bitkinin nasıl ki bir çok şifa verme özelliği varsa Avşar otunun da başka özellikleri vardır. Avşar otunun kusturucu etkisi dışında ateş düşürücü, idrar arttırıcı, adet söktürücü ve müshil etkileri bulunmaktadır.", BilimselAdi: "Bilimsel Adı : Asarum Europaeum")
        bitkiler.append(avsarotu)

        let agachatmi = Bitkiler(Adi: "Ağaçhatmi", Tanim: "Ebegümecigiller (Malvaceae) familyasındandır.Anavatanı Çin, Hindistan ve Kore´ dir.Ortalama olarak 100 yıl yaşayabilir.Habitusu yuvarlak tepeli çalı formundadır. Küçük boylu, yaprağını döken bir ağaçtır.5 metre boya, 50 cm gövde çapına ulaşabilir.Gövde kabuğu açık gri renkli ve çatlaksızdır.Yaprakları yeşil renkli üç loblu ve sivri uçludur.Kısa saplı hermafrodit, beş taçlı ve geniş huni şeklindeki çiçekleri, beyaz, pembe, eflatun veya kırmızı renklenme gösterir.Meyveleri kapsül şeklinde ve beş gözlüdür.Yüz gün civarında bir süreyle en uzun çiçeklenme süresi olan bir süs ağaçtır. Çok çekici ve güzel çiçekleri, azim, kibarlık ve dayanıklılığı temsil eder.", Image: UIImage(named: "agachatmi")!, Faydalari: "Kış aylarında rastlanan ve halk arasında kuru öksürük olarak adlandırılan inatçı öksürüklerle mücadelede oldukça yararlı olduğu gözlemlenmiştir.Gastrit, peptik ülser, gastroözofageal reflü gibi mide rahatslıklarına karşı etkili bir bitkidir.Enfeksiyonlara karşı iyi bir savaşçı olan Hatmi çiçeği, özellikle soğuk algınlığında ve gripte etkilidir.Hatmi çiçeğinin yaygın kullanımlarından biri ağrı giderici olarak topikal olarak uygulanmasıdır. Özellikle yapraklarının ağrı kesici etkisi olduğu bilinmektedir.Sakinleştirici etkisi de olan hatmi çiçeği, bünyesinde barındırdığı uçucu yağlar sayesinde çay olarak tüketildiğinde uyku problemi yaşayanlarda faydalı olur.Böbrek iltihaplarına iyi gelen bu bitki ayrıca iyi bir idrar söktürücüdür.İltihaplara karşı oldukça etkili olduğu bilinen hatmi çiçeği; prostat, sinüzit, zona, mide ve bağırsak iltihaplarına iyi gelmektedir.", BilimselAdi: "Bilimsel Adı : Hibiscus Syriacus")
        bitkiler.append(agachatmi)

        let aleovera = Bitkiler(Adi: "Aleo Vera (Tıbbi sarısabır)", Tanim: "Aloe vera sağlık açısından birçok faydası olan sulu bir bitkidir. Alternatif tıpta birçok durumun tedavisi için kullanılmıştır. Aloe vera, sıcak ve kuru iklimlerde yetişen kaktüs benzeri bir bitkidir. Kozmetik ve tıbbi amaçlar için kullanılan aloe vera bitkisinin ana etkeni aloe jelidir.Aloe vera kullanımının geçmişi yaklaşık 6.000 yıl öncesine kadar Mısır'a dayanır. “Ölümsüzlük bitkisi” olarak adlandırılan aloe vera, bir zamanlar firavunların cenazesinde hediye olarak verilmiştir. Mısır dışında, diğer ülkelerde de sağlık yararları nedeniyle aloe vera kullanılmıştır.", Image: UIImage(named: "aleovera")!, Faydalari: "Aloe Vera sindirim sistemini kuvvetlendirir.Aloe vera yağı veya aloe vera sabunu cilde uygulandığında özellikle kuru ciltlerde sık karşılaşılan dökülme ve kızarıklık sorunlarını iyileştirmeyi sağlar.Yanıklar, deri iltihaplanmaları ve güneş ışınlarının zararlı etkileri sonucu oluşan yanıklar aloe veranın bileşenleri sayesinde tedavi edilmektedir.Kolon kanseri riskini azaltır.Bağırsak hastalıkları tedavisinde kullanılır. Bağırsak mantarlarını giderir.Böbrekleri hastalıklardan koruma etkisine sahip olan aloe veranın vücuttaki enzimleri depolama özelliğine sahip olduğu bilinmektedir.Enfeksiyon oluşumunu engeller. Metabolizmayı hızlandırır.Kana oksijen aşılayan aloe vera bitkisinin iltihap problemlerinde de tedavi edici yapıya sahip olduğu gözlemlenmiştir.Aloe vera bitkisi kanser tümörlerinin oluşmasını başlangıç aşamasında engelleyebilir.Kolesterol seviyesini dengede tutar.Kalp damar sağlığına faydalıdır.Kan akışının hızlanmasını sağlayarak damar tıkanıklığını önler.Aloe vera jeli ve sabunu ufak cilt alerjileni tedavi eder.Aloe vera depresyon ve ruhi bozukluklara neden olan sorunları ortadan kaldırabilir.Aloe vera bitkisi radyasyon etkilerini azaltabilir.Aloe veranın mikrop öldürücü özelliği vardır.Egzama ve sedef hastalığına iyi gelir.Göz iltihaplarını aloe vera suyu ile hafifletir.", BilimselAdi: "Bilimsel adı: Aloe vera")
        bitkiler.append(aleovera)

        let alic = Bitkiler(Adi: "Alıç", Tanim: "Alıç bitkisi önemli tıbbi bitkiler arasında yer alır. Alıç fidanı, çiçekleri ve meyvesi antioksidan özelliğe sahip flavon, şekerler, eter yağı, organik asitler, saponin ve vitaminler içerir. Kalbin düzenli çalışmasını destekleyen alıç çeşitleri kalp ve damar fonksiyonlarının düzenlenmesinde etkinidir.Yüksek oranda demir, magnezyum, potasyum, fosfor ve kalsiyum mineralleri içeren alıç, özellikle C vitamini açısından oldukça zengindir. Geçmiş zamanlarda sağlığa faydaları nedeniyle ilaç olarak tercih edilen meyve, günümüzde de birçok ilacın üretiminde kullanılmaktadır.", Image: UIImage(named: "alic")!, Faydalari: "Damarları genişletici etkiye sahip olan alıç, kalp damarlarından kanın kolay pompalanmasını sağlayarak dolaşımı kolaylaştırır ve böylece kalbin yükünü hafifletir.Kalp krizi riskini önler ve damar sertliğine karşı vücudu korur.Kalp atışlarının düzenlenmesinde önemli fonksiyon vardır.Sakinleştirici etkiye sahip olan alıç, sinir bozukluğunu önler ve sinirsel çarpıntı gibi rahatsızlıklarda etkindir.Beyine giden damarların genişlemesini sağlayarak kan akışını arttırır.Uykusuzluğu önler.Tansiyon düşürücü etkisinden dolayı yüksek tansiyon hastalarına önerilir.Mideyi rahatlatıcı etkisi vardır.İdrar söktürücüdür ve cinsel performansı arttırır.", BilimselAdi: "Bilimsel Adı : Crataegus Monogyna")
        bitkiler.append(alic)

        let anason = Bitkiler(Adi: "Anason", Tanim: "Günümüzde tohumu, bitkisi ve yağının pek çok faydasının olduğu bilimsel araştırmalarla gösterilmiştir. Başta rakı üretimi ve diş macunu yapımı olmak üzere pek çok alanda kullanılmaktadır.", Image: UIImage(named: "anason")!, Faydalari: "Anason halk arasında iştah açıcı, uyku verici, anne sütünü artırıcı, gastrik spazm şikâyetlerini ve barsak gazlarını giderici, balgam söktürücü, antibakteriyel, karaciğeri koruyucu olarak kullanılmaktadır.Çay olarak içilen anason da hafif spazmlı gastrointestinal şikayetler (mide ve bağırsak şikayetleri) ve epigastrik şişkinlik gibi bazı dispeptik rahatsızlıkların tedavisinde ve üst solunum yolu enfeksiyonlarının tedavisine ek olarak iyi gelebileceğini gösteren çalışmalarla ortaya konulmaktadır.Anasondan elde edilen uçucu yağın yüzde 70-90 anaethole içeren ekstreleri de uyarıcı, tonik, aromatik özellikleri ile bilinmektedir. Karaciğer koruyucu (Hepatoprotektif) etkisi olduğu da çalışmalarda ileri sürülmektedir.", BilimselAdi: "Bilimsel Adı : Pimpinella anisum")
        bitkiler.append(anason)

        let aynisafa = Bitkiler(Adi: "Aynı Safa", Tanim: "Ana vatanı Akdeniz kıyıları ve Avrupa olarak bilinse de aynısefa bitkisi her coğrafyada yetişebilen, turuncu çiçekli bir bitkidir. Alternatif tıpta kullanılan birçok bitki gibi aynısefa; vitamin ve şifa kaynağıdır. Aynısefa; Latince ismi “calendula officinalis” olan aynı zamanda nergis olarak da bilinen bir bitkidir. Bir yıllık olan bu bitkinin özelliği çiçeğidir. Yetişmesi oldukça basittir. Her yıl kolayca aynısefa tohumlarıyla yetişmektedir. Oldukça güzel renklere sahip olan aynısefa bitkisi hem bahçeleri süslemekte hem de sağlığa oldukça faydalı yararlar sunmaktadır.", Image: UIImage(named: "aynisafa")!, Faydalari: "Ağrı kesici olarak kullanılır. Ağrıyan her bölgeye merhemi sürülebilir. Ağrılar hafifler, gerginlik ve sertlikler gider. İdrar tutukluluğunu giderir. Terletici özelliğe sahiptir.Aybaşı kanını söktürür ve düzene sokar.Aynısafa çayı, kan temizleyici bir bitki çayı olarak, mikroplu sarılıkta çok yararlı bir bitkidir.12 parmak bağırsağı ülserlerinde kullanılabilir.Kaşıntılı ayak mantarlarını yok etmede çok iyi gelir.Kan çıbanı, derideki pigment lekeleri, ihtiyarlık lekeleri için öz suyu deriye sürülür, çok faydalıdır.Ezik ve yaralara sürüldüğünde iyileşme çabuklaşır. Aynı şekilde yanık yaralarında da emniyetle kullanılır.Süre gelen egzamaların iyileşmesinde etkin bir bitkidir.Bebek bezlerinden oluşan isiliklerin lokal tedavisinde etkindir.Diş etlerine sürüldüğünde dişleri saran reçine plakalarından dolayı oluşan rahatsızlıkları giderir.", BilimselAdi: "Bilimsel Adı : Calendula Officinalis")
        bitkiler.append(aynisafa)

        let ayrikotu = Bitkiler(Adi: "Ayrık Otu", Tanim: "Bilimsel kaynaklarda “agropyrum repens” adı ile bilinen ayrık otu; yabani ve otsu yapıya sahip bir bitki türüdür.Kendine verimli bir alan oluşturduğunda neredeyse 1 metre kadar uzayabilme özelliği olan ayrık otu, buğdaygillerden olan bir türdür.Sarı ve beyaz arasında bir renge bürünen ayrık otu kökü, şifa niyetine toplanmaktadır. Bitki yeşil renklidir, kötü ise daha açık tonlardadır.Ayrık otunun en üst bölümünde çiçekli ve hafif kuru bir başak yer alır. Sapı, kökü ve başağı ile beraber hayvanlar için bitkisel protein kaynağı olmaktadır.", Image: UIImage(named: "ayrikotu")!, Faydalari: "Böbreklerde meydana gelen taş problemini giderir. Taşın vücuttan atılmasını kolaylaştıran ayrık otu aynı zamanda böbrek ve mesanede varlık gösteren iltihapları yok eder, meydana gelmesi muhtemel iltihabı önler.İdrar söktürücü özellikte bir bitkidir.İltihap kaynaklı hastalıkların tedavisine doğal bir yardımcı olarak kullanılır.Böbreklerde meydana gelen sancıların giderilmesine yardımcı olur. Bununla birlikte böbrek yetmezliği olan hastalara doktor kontrolünde kullanılması önerilen bir bitkidir.Kanı toksik maddelerden temizleyerek vücuttan dışarı atılmalarını sağlar.Antiseptik özellikle bir bitkidir.İçeriğinde yer alan maddeler sayesinde prostat büyümesini önlendiği düşünülür.Romatizma kaynaklı semptomların şiddetinin azaltılmasına, rahatlamaya yardımcı olur.Egzama ve cilt hastalıklarının iyileşmesini sağlar.", BilimselAdi: "Bilimsel Adı : Elymus repens")
        bitkiler.append(ayrikotu)

        let besparmakotu = Bitkiler(Adi: "Beşparmak Otu", Tanim: "Yapraklarının rozet biçiminde olduğu beşparmak otunun yaprakları ise gümüş renkli olmaktadır. Uzun sağlı çiçeklere sahip olan beşparmak otunun çiçekleri ise yaprakları arasında yetişmektedir. Yapraklarının ve köklerinin yaz ayları sonlarında toplandığı ve kurutulduğu beşparmak otu özellikle genel sağlık açısından sunduğu birçok harika faydaları sayesinde geçmişten günümüze kadar birçok toplumlar tarafından tedavi yardımcı faktörü olarak kullanılmıştır. ", Image: UIImage(named: "besparmakotu")!, Faydalari: "İshali kesiyor.Mide rahatsızlıklarını hafifletiyor.Vücudu güçlendiriyor.Bademcik ve boğaz ağrılarına iyi geliyor.Diş ağrılarını azaltıyor.Diş etlerini kuvvetlendiriyor.Cilt lekelerini ortadan kaldırıyor.Cildi yumuşatıyor.", BilimselAdi: "Bilimsel adı: Potentilla erecta")
        bitkiler.append(besparmakotu)

       let beyaznilufer = Bitkiler(Adi: "Beyaz Nilufer", Tanim: "Beyaz nilüfer, nilüfergiller familyasından bir nilüfer türü. Kuzey Amerika, Avrupa ve Asya'nın batı ve orta kısımlarında genellikle yavaş akan ırmak kenarları, göl ve göletlerde doğal olarak yetişir, soğuğa dayanıklı bir türdür.Yaprakları yuvarlak bir tepsi şeklinde sadece sap kısmı hafif dar üçgen şeklinde açıktır. Yaprakların alt kısmı kahverengimsi veya kırmızımsı esmer, üst yüzeyi koyu yeşil, derimsidir. Fincan şeklinde, ortası sarı, kar beyazı çiçekleri ile gösterişli bir türdür. Çabuk büyüyen ve sudan çıkma eğiliminde olduğundan, küçük sığ havuzlar için uygun değildir. 40–90 cm derinlikteki suya gereksinim gösterir.", Image: UIImage(named: "beyaznilufer")!, Faydalari: "Ateşi çıkan kişiler içtiği takdirde beynin hipotalamus bölgesini uyarır ve vücut sıcaklığının normal seviyelere düşmesini hızlandırır. Kalp atışlarını düzenler ve başta aritmi olmak üzere birçok kalp hastalığına yakalanma riskini azaltır. Bu bitkiler B vitamini açısından oldukça zengindir. Periyodik aralıklarla tüketilirse anemi ve bu hastalığa bağlı olarak ortaya çıkan halsizlik, kronik yorgunluk gibi belirtileri en aza indirger. Nilüfer çiçeği fiziksel olduğu kadar ruhsal sağlığa da olumlu etkileri olan bir bitki türüdür. Sinir zafiyeti ve öfke kontrolü olan kişiler günde bir ya da iki fincan nilüfer çayı içebilir. Sakinleştirici bir etkiye sahiptir. Yatmadan birkaç saat önce içildiği takdirde uyumayı kolaylaştırır. ", BilimselAdi: "Bilimsel Adı : Nymphaea Alba")
        bitkiler.append(beyaznilufer)

        let beyazyulaf = Bitkiler(Adi: "Beyaz Yulaf", Tanim: "Beyaz yulaf (Avena sativa), Avena'ların (yulaf) alt grubuna ait yulaf türüdür. Dünyada kültür yulaflarının 2/3'ü beyaz yulaflardır. Kırmızı yulaflar (Avena byzantina Koch.) da yaygın olarak kültürü yapılan yulaf türlerindendir. Kırmızı yulaf beyaz yulafa göre daha iri salkıma, başakçıklara ve daha uzun boya sahiptir. Taneleri ve başakçık dış kavuzu da daha koyu renktedir. Çimlenme sıcaklıkları 4-50 °C iken optimum sıcaklığı 20-25 °C'dir", Image: UIImage(named: "beyazyulaf")!, Faydalari: "Beyaz Yulaf Kilo Vermenize Yardımcı Olur.Beyaz Yulaf Cilt Bakımında Yardımcı Olur.Kansızlığa Karşı Etkilidir.Duygusal Durumu Dengeler.Gözlerimizin İyi Görmesine Yardımcı Olur. Enerji Takviyesinde Yardımcı Olur.Kolesterol Seviyesini Düşürür.Kalp Ve Damar Hastalıkları Riskini Azaltır.", BilimselAdi: "Bilimsel Adı : Avena Sativa")
        bitkiler.append(beyazyulaf)

        let biberiye = Bitkiler(Adi: "Biberiye", Tanim: "Biberiye, her ne kadar kurutulmuş baharat haliyle biliniyor olsa da aslında iğneye benzer dar yapraklara sahip yeşil bir çalı türü. Bu bitkinin boyu en gelişkin döneminde bir ila iki metre arasında olabiliyor. Ayrıca biberiye, sahip olduğu yeşil rengi her mevsim koruyabiliyor. Biberiye bitkisinin mavi-eflatun renkli çiçekleri bulunuyor. Fakat bitkinin baskın kokusu çiçeklerinden değil, yapraklarından yayılıyor. Latince adı Rosmarinus Officinalis olan biberiye, ballıbabagiller familyasında yer alıyor. Rosmarinus, Latin dilinde “denizin çiği” anlamına geliyor. Bu sebeple de botanik bilimciler, deniz kıyısını çok seven biberiyeye bu ismi veriyor. Bitkinin Türkçede yaygın bilinen adı biberiye olmakla birlikte, bazı yörelerde “kuş dili otu” olarak da adlandırılıyor.", Image: UIImage(named: "biberiye")!, Faydalari: "Yüksek miktarda A vitamini ve C vitamini yanında B6 vitamini, B1 vitamini ve B9 vitamini içeriyor. Magnezyum, kalsiyum, bakır, manganez ve demir minerallerini de içeriğinde barındıran bitkinin insan sağlına bilinen birçok faydası bulunuyor.İltihap giderici özelliğiyle sindirimi hızlandırıyor ve kolaylaştırıyor.Kas spazmI ve ağrı giderici özelliğiyle biliniyor.Kalp sağlığını koruyor.Sinir fonksiyonlarının iyileşmesine yardımcı oluyor. Özellikle ataksi, bunama, Alzheimer ve Parkinson gibi hastalıkların önlenmesinde kullanılıyor.Kilo verme ile ilgili etkili sonuçlar veriyor.Suyun vücuttan atılım hızını artırarak, idrar söktürüyor.Biberiyede bulunan karnosol, rosmarinik, betülinik ve karnosik-asit kansere karşı koruyucudur. Bitkinin özleri cilt kanseri, lösemi, meme ve karaciğer kanseri, prostat, mesane, yumurtalık, kolon, akciğer, ve pankreas kanserlerine karşı etkili sonuçlar veriyor.", BilimselAdi: "Bilimsel adı: Salvia rosmarinus")
        bitkiler.append(biberiye)

        let bostanotu = Bitkiler(Adi: "Bostan Otu(Şevkati)", Tanim: "Dikenli yapısı olan şevketi bostan otu, sarı çiçek açar. Papatyagiller familyasının üyesi olan şevketi bostan otu, son zamanlarda popüler olan bitki türlerinden biridir. Tüketimi yaygınlaşan şevketi bostan otu, kendi mevsiminde toplandıktan sonra, kurutularak tüketilebilir. Kültür bitkisi olan şevketi bostan otu, solunum yolu rahatsızlıklarında kullanılmaktadır.", Image: UIImage(named: "bostanotu")!, Faydalari: "Bostan otu iştah açar.Bostan otu düzenli tüketildiğinde karahindiba otu gibi vücuda zindelik ve canlılık verir.Sindirim sorunlarına iyi gelir ve mideyi rahatlatır. Bostan otundan yapılan çay karın ağrısını geçirir.Bostan otunun çayı madımak bitkisi gibi böbrek taşı düşürmeye yardımcı olur.Bostan otunun suyu yaraları temizlerken kullanılır. Lapa yapılıp yaraların üzerine sürülürse yaraları çabuk iyileştirir.Doku ve damar büzücü etkisiyle kanamaları durdurur.", BilimselAdi: "Bilimsel adı: Cnicus benedictus")
        bitkiler.append(bostanotu)

        let bugday = Bitkiler(Adi: "Buğday", Tanim: "Buğday yani Triticum, Buğdaygiller (Poaceae) ailesinin tüm dünyada ıslahı yapılan otsu ve tek yıllık bitkisi olarak anılmaktadır. Karasal iklimde yetiştirilebilen buğday, mısırla beraber dünya genelinde en çok ekilen ve büyütülen tarım ürünlerinin arasında ilk sıralarda gelmektedir.Buğday ülkemizde geniş bir yaşam alanı bulmuştur ve iklim şartlarına da uygun olması sebebiyle fazlasıyla yetiştirilmektedir. Yurdun neredeyse her bölgesinde az da olsa buğday yetiştirilmektedir.", Image: UIImage(named:"bugday")!, Faydalari: "Saç ve cilt sağlığı için çok önemli bir besindir.E vitamini açısından da zengin olan besin cilde esneklik vererek, yaşlanmayı geciktirir.Ciltteki akne ve sivilcelerin geçmesine yardımcı olur. Birçok kozmetik ürünün içerisinde buğday özü olduğuna dikkat etmişsinizdir. Eski zamanlarda da cilt sorunlarını çözmek amacıyla ezilerek lapa yapıldığı ve maske şeklinde uygulandığı bilinmektedir.Lif oranı yüksek olan besin bağırsak hareketlerini düzenler, sindirim sisteminin sağlıklı çalışmasını sağlar. Kronik kabızlık sorununa iyi gelir.Kalp ve damar hastalıklarına yakalanma riskini azaltır.Göz sağlığı için çok faydalıdır.Kan şekerini düzenlediği için şeker hastaları için ideal bir besindir.Deri başta olmak üzere birçok kanser türüne karşı koruma sağlar.E ve B vitaminleri sayesinde beyin sağlığı için faydalıdır. BAlzheimer ve bazı zihinsel hastalıklara yakalanma riskini azaltır.Vücuttaki iltihap ve enfeksiyonu temizler.Diş ve dişeti hastalıkları için iyidir.Kan akışını düzenler.Midede meydana gelen hazımsızlık ve şişkinlik sorunlarına iyi gelir.", BilimselAdi: "Bilimsel adı: Triticum aestivum")
        bitkiler.append(bugday)

        let burcak = Bitkiler(Adi: "Burçak", Tanim: "Burçak (Vicia ervilia), baklagiller familyasından 25–30 cm. boyunda, 8-10 çift yaprakçıktan oluşan, taneleri çok küçük, köşeli ve kırmızımtrak renkte olan bitki.Yılık ve çok yıllık çeşitleri olan bitki dünyada Kuzey Yarım Küre'nin değişik bölgeleri ile Güney Tropik bölgelerde yetişir.Türkiye'de Ege Bölgesi ve Orta Anadolu'da yeşil yaprakları koyunların beslenmesinde, taneleri ise damızlık boğalar için yem olmak üzere yetiştirilir.", Image: UIImage(named: "burcak")!, Faydalari: "Unu sirke ile karıştırılıp içilirse idrar söktürür.Bünyesi zayıf olanlara helva yapılıp yedirilirse şişmanlatır.Taneleri isilik ve mayasıla sürülür.Ciğer, dalak tıkanıklığına çok faydalıdır.Cinsel isteği artırır.Süt yapar.Ciğeri besler.", BilimselAdi: "Bilimsel adı: Vicia ervilia")
        bitkiler.append(burcak)

        let ceviz = Bitkiler(Adi: "Ceviz", Tanim: "Ceviz, Juglandaceae familyasının Juglans cinsinden, tek tüysü yaprakları karşılıklı dizilmiş ve aromatik kokulu ağaç türlerinin ve bu ağaçların meyvelerinin ortak adı.Kışın yaprağını döken ağaçlardır. Genç sürgünlerin özü bölmelidir. Tomurcuklar az sayıda pullarla örtülmüştür. Yaprakçıkların kenarları bazı türlerde ince dişli, bazılarda ise düzdür (tam kenarlı). Yaprakçık sayısı türlere göre 5-23 arasında değişir.Sonbaharda olgunlaşan büyük çekirdekli sulu meyvenin iç kısmı 2 bölmeye ayrılmıştır. Tohum 2 loplu, yağlı ve lezzetlidir. Odununun özü koyu, dış kısmı açık renkli, ağır ve güzel cila kabul eden odunları vardır.", Image: UIImage(named: "ceviz")!, Faydalari: "Magnezyum, çinko, bakır, demir, fosfor, bir dizi vitamin içeren ceviz; iyi yağlar, folik asit, demir ve potasyum açısından zengindir. Sağlıklı ve dengeli beslenme konusunda gerçek bir süper gıdadır.Antioksidanların bolluğu nedeniyle, yaşlanmaya bağlı hastalıkları, kanseri ve kardiyovasküler sistem hastalıklarını önlendiği bilinmektedir.Ceviz glütensiz bir diyet için de uygundur. Vejeteryanlar ile veganlara bir dizi besin ve vitamin desteği sağlamaktadır.Yapılan araştırmalarda, düzenli ceviz tüketenlerde kötü kolesterol olarak tanımlanan LDL’nin yemeklerden sonra ortaya çıkan oksidatif hasarını önlediği belirlenmiştir.", BilimselAdi: "Bilimsel adı: Juglans")
        bitkiler.append(ceviz)

        let dari = Bitkiler(Adi: "Darı", Tanim: "Panicum miliaceum ailesinin bir üyesi olan darı, hem hayvanlar için hem de insanlar için iyi bir besin kaynağıdır. Bahar aylarında darının tohumları toprakla buluşturulur ve yaz mevsiminin sonraların da hasadı yapılır. Ülkemizin genele olarak tüm şehirlerinde yetiştirilen bu bitki genel olarak, tek yıllık bir bitkidir.  Aslında mısırın bir çeşidi olarak karşımıza çıkar. Süt mısıra göre daha serttir, patlatmalık mısıra göre daha büyük ve iri tohumlara sahiptir. Darının genelde unu ve yemi yapılır. Darı ile beslenen hayvanların süt verimleri daha yüksektir ve daha kalitelidir.Darının pek çok çeşidi mevcuttur. En fazla yaygın olarak kullanılanı ise hiç kuşkusuz inci darılardır. İnci darıların besleyicilik özellikleri diğer iri taneli darılara göre daha fazladır. Bu nedenle ülkemizde de sıklıkla yetiştiriciliği yapılmaktadır.", Image: UIImage(named: "dari")!, Faydalari: "Darı kolesterolün düşmesine yardımcı olur.Hemoroid rahatsızlığı olan kişiler için oldukça faydalıdır.Sağlıklı büyümeye yardımcı olur ve vücudu daha dinç kılar.İçerdiği demir vitamini ile kansızlık gibi rahtsızlıkların önüne geçer.İçerdiği karbonhidratlar nedeni ile gün boyu enerji verir.Darı kilo almanıza yardımcı olur.Darı içerdiği yararlı nişastalar sayesinde cilt tonunu açar ve cilt rengini eşitler.Cildi güneşin uv ışınlarına karşı korur.Darı yanaklarda oluşan sarkmaları önler.", BilimselAdi: "Bilimsel adı: Panicum miliaceum")
        bitkiler.append(dari)

        let defne = Bitkiler(Adi: "Defne", Tanim: "Defnegiller familyasındandır. Yaz kış yaprağını dökmeyen bir bitkidir. Ülkemizin Akdeniz kıyıları başta olmak üzere Ege, Marmara ve Karadeniz kıyıları ile bu kıyıların iç kısımlarında yetişir. 6-8 metre'ye kadar boylanabilen defne, yuvarlak tepeli ve sık dallı olarak gelişir. 8-10 cm. uzunluktaki oval ve sert, üst yüzü parlak, kenarları dalga görünüşlü ve almaşık dizili koyu yeşil yaprakları vardır. İlkbaharda açan sarımsı ya da yeşilimsi beyaz renkteki küçük çiçekleri olgunlaşınca rengi koyu mor, tek tohumlu ve etli meyvelere dönüşür. Bitkinin yuvarlak kesitli ve mor-kahverengi olan gövdesi, zamanla odunlaşır ve rengi griye döner. Defne, gövde çelikleriyle çoğaltılır.", Image: UIImage(named: "defne")!, Faydalari: "Yiyeceklere lezzet vermesi için baharat olarak kullanılır.Ateşi düşürür, terletir.Çayı içilirse şiddetli baş ağrılarına iyi gelir.Kaynatılan suyuna oturmak suretiyle mesane hastalıklarını iyileştirir.Kaynatılan suyuyla banyo yapılırsa vücudu uyarıp zindeleştirir,kadınlarda görülen regl sancılarına ve akıntılara iyi gelir.İştahı açar, hazmı kolaylaştırır.Yaprakları sirke ile kaynatılıp gargara yapılırsa diş ağrılarını keser.Kuru bakliyatların ( fasulye, nohut, mercimek vb.) içine konursa güvelenmesini veya böceklenmesini önler.", BilimselAdi: "Bilimsel adı: Laurus")
        bitkiler.append(defne)

        let demirhindi = Bitkiler(Adi: "Demirhindi", Tanim: "Yaygın olarak Hint hurması olarak bilinen demirhindi, ana vatanı Habeşistan olan, başta Hindistan ve Mısır olmak üzere dünya üzerinde birçok yerde yetiştirilen bir bitki aslında. Bu bitkinin tıpkı keçiboynuzuna benzeyen meyveleri de bitkiyle aynı ismi taşıyor ve işte bu meyveleri sebebiyle bitkinin ünü her geçen gün yayılıyor.Zira demirhindi meyvesinin içeriğinde A, B1, B2, B3, B5 , B6 ve C vitaminleri bulunduğu, bu vitaminlere ek olarak demirhindinin potasyum, kalsiyum, pektin, demir, sodyum, magnezyum, fosfor gibi mineraller bakımından da zengin olduğu biliniyor. Hal böyle olunca demirhindinin faydaları da saymakla bitmiyor.", Image: UIImage(named: "demirhindi")!, Faydalari: "İçeriğindeki vitamin ve minerallerden aldığı destekle demirhindi, antioksidan özellikler gösteriyor, başta soğuk algınlığı, grip, nezle gibi hastalıklar olmak üzere birçok hastalığa karşı koruyucu etki oluşturuyor.Bu özelliği sayesinde kalp ve damar hastalıklarına karşı da vücudu koruyor.Çay gibi demlenip gargara olarak kullanıldığında boğaz ağrılarını hafifletiyor.Vücutta adeta doğal bir ağrı kesici etkisi oluşturduğundan eklem ağrılarına da iyi geliyor.Demirhindinin kilo vermeye yardımcı olduğu da söyleniyor.Sindirim sisteminin sağlıkla çalışmasına destek oluyor, hazmı kolaylaştırıyor.Bu etkisiyle kabızlık sorunu yaşayanlara da destek olduğu biliniyor.Demirhindinin göz, safra kesesi ve bağırsak sağlığı üzerinde de olumlu etkileri bulunduğu söyleniyor.", BilimselAdi: "Bilimsel adı: Tamarindus indica")
        bitkiler.append(demirhindi)

        let ebegumeci = Bitkiler(Adi: "Ebegümeci", Tanim: "Halk arasında develangir, kazan karası ya da ilbik gibi farklı isimlerle anılan, genelde mor çiçekleri ile bilinse de aslında pembe, beyaz gibi farklı renklerde çiçek açan türleri de bulunan bir bitki. Doğada kendiliğinden yetişebilen bu bitkiye ülkemizin dört bir yanında rastlamak mümkün oluyor aslında ama faydalarından yararlanmak isteyenler için en doğrusu ve aslında en kolayı aktarlardan temin etmek oluyor.", Image: UIImage(named: "ebegumeci")!, Faydalari: "Ebegümeci güçlü antioksidan özellikler gösteriyor.Kış hastalıklarına karşı vücudu koruyor.Göğsü yumuşatıcı bit etki gösteriyor.Doğal bir ateş düşürücü olarak görülüyor.Şişliklerin daha hızlı iyileşmesine destek oluyor.Ebegümeci, ağız ve diş sağlığını olumlu yönde etkiliyor.Cildin temizlenmesine yardımcı oluyor.Yaraların daha hızlı iyileşmesine destek oluyor.Sivilce ve cilt lekelerine de iyi geliyor.", BilimselAdi: "Bilimsel adı: Malva Sylvestris")
        bitkiler.append(ebegumeci)

        let egreltiotu = Bitkiler(Adi: "Eğrelti Otu", Tanim: "Eğrelti otugillerden, kumlu yerlerde yetişen, 150 santimetre kadar yükselebilen, tıpta bağırsak kurtlarını düşürmek için kullanılan çok yıllık ve otsu bir bitkidir. 10 binden fazla türü bulunan bu bitki, ılıman bölgelerde yetişir. Her ne kadar çok çeşidi olsa da genel olarak dişi eğrelti otu ve erkek eğrelti otu olarak ikiye ayrılır. Tıpta birçok alanda da kullanılan eğrelti otu zehirli bir bitki olarak bilinir. ", Image: UIImage(named: "egreltiotu")!, Faydalari: "Kemik sağlığını destekler.Sinir sisteminin fonksiyonunu destekler.Akciğerleri iyileştirmeye yardım eder.Hazımsızlığı giderir.Zengin demir kaynağı.Kanserli hücre gelişimini önleyebilir.Kalp sağlığına destekler.", BilimselAdi: "Bilimsel Adı : Pteridophyta")
        bitkiler.append(egreltiotu)

        let ekinezya = Bitkiler(Adi: "Ekinezya", Tanim: "Bitkisel bir takviye olarak adı gün geçtikçe daha fazla duyulan Ekinezya oldukça popüler bir bitkidir ve papatya ailesindeki otsu çiçekli bitki ailesinin bir türüdür. Diğer ismi de Amerikan koni çiçeğidir. Dokuz vahşi türü doğu ve orta Kuzey Amerika'da yaşar; nemli ya da kuru çayırlarda ve açık ağaçlık alanlarda yetişir. Ekinezya, çok yıllık bir bitki olduğu için olgunlaşması yıllarca sürer ve olgunlaştığında ise yaklaşık olarak 30-60 santimetre boyuna ulaşır. Bu bitki türü, cinsine bağlı olarak mor ya da pembe çiçeklere sahip olur. Çiçeğin merkezinde, dikenli ve koyu kahverengi ile kırmızı renkli bir tohum kafası (koni) vardır. Ayrıca Ekinezya'nın cins mor, soluk mor ve dar yapraklı mor koni çiçeği olmak üzere 3 türü vardır. Bu türlerin hepsi yaz başından yaz sonuna kadar yaşayan büyük kırmızı yapraklara sahiptir.", Image: UIImage(named: "ekinezya")!, Faydalari: "Ağrı kesici özelliği vardır.Bağışıklık sistemini güçlendirir.Grip ve nezleye karşı koruma sağlarEnfeksiyonları önlerDetoks etkisi vardırVücut kırgınlığını ve halsizliği giderirBöcek ve yılan ısırığında tedavi edici etkisi vardırDiş ağrısını hafifletebilir.Kanserden koruma özelliği vardır.", BilimselAdi: "Bilimsel adı: Echinacea")
        bitkiler.append(ekinezya)

        let feslegen = Bitkiler(Adi: "Fesleğen", Tanim: "Ballıbabagiller familyasına ait bir bitki olan fesleğen, yemeklerin içerisinde sık sık kullanılır. Kökeni Asya'ya dayanan bu bitki, evlerde en çok yetiştirilen besinlerden biridir. Mis gibi kokusuyla dikkatleri üzerine çeken fesleğen, halk arasında 'reyhan' olarak da bilinir. 30-40 cm boyuna kadar ulaşan fesleğen, taze bir şekilde tüketilir.", Image: UIImage(named: "feslegen")!, Faydalari: "Hazımsızlık ve mide kramplarına karşı etkilidir. Mide bulantısını kısa sürede geçirir.Anti stres etkisi vardır. Sinirlilik, uykusuzluk, stres ve depresyona karşı etkilidir.Pamukçuk ve ağız içi yaralarının iyileşmesine yardımcı olur.Epilepsi, migren ve baş ağrısı tedavisinde etkilidir.Bal ve zencefil ile kaynatıldığında solunum yolu hastalıklarına iyi gelir. Grip, astım, bronşit gibi rahatsızlıklara karşı etkilidir.Balgam söktürücüdür.A vitamini bakımından zengindir. Bu sayede ağız boşluğu ve akciğer kanserine yakalanma riskini düşürür.İçeriğinde bulunan K vitamini sayesinde kemiklerin güçlenmesine yardımcı olur.Fesleğen suyu, mantar gibi deri hastalıklarının tedavisinde kullanılır.Demir, manganez, potasyum ve bakır mineralleri bakımından zengindir.Böcek ve arı ısırıklarında fesleğen suyu tedavi amaçlı kullanılabilir.", BilimselAdi: "Bilimsel adı: Ocimum basilicum")
